import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAXN = 10005;

    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long C(int n, int m) {
        if (n < m) return 0;
        return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
    }

    static long invC(int n, int m) {
        if (n < m) return 0;
        return inv[n] * fac[m] % MOD * fac[n - m] % MOD;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        fac[0] = 1;
        for (int i = 1; i < MAXN; i++) fac[i] = fac[i - 1] * i % MOD;
        inv[MAXN - 1] = pow(fac[MAXN - 1], MOD - 2);
        for (int i = MAXN - 2; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;

        String A = br.readLine();
        String B = br.readLine();

        int n = A.length();
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            if (A.charAt(i) == '1' && B.charAt(i) == '1') cnt++;
        }

        long ans = 0;
        for (int i = 0; i <= cnt; i++) {
            long coef = C(cnt, i);
            long num = pow(2, i) * pow(2, i) % MOD;
            long den = 1;
            for (int j = 0; j < i; j++) den = den * (i - j) % MOD;
            for (int j = 0; j < i; j++) den = den * (cnt - j) % MOD;
            den = pow(den, MOD - 2);
            num = num * den % MOD;
            num = num * coef % MOD;
            ans = (ans + num) % MOD;
        }

        pw.println(ans * pow(pow(2, cnt), 2) % MOD);
        pw.close();
    }
}
