import sys
input = sys.stdin.readline

mod = 998244353

def qpow(a, b):
    ans = 1
    while b:
        if b & 1:
            ans = ans * a % mod
        a = a * a % mod
        b >>= 1
    return ans

def inv(a):
    return qpow(a, mod - 2)

def C(n, m):
    if n < m:
        return 0
    return fac[n] * inv(fac[m] * fac[n - m] % mod) % mod

fac = [1] * (10 ** 5 + 5)
for i in range(1, 10 ** 5 + 5):
    fac[i] = fac[i - 1] * i % mod

def solve():
    s1 = input().strip()
    s2 = input().strip()
    n = len(s1)
    cnt = 0
    for i in range(n):
        if s1[i] == '1':
            cnt += 1
    if cnt == 0:
        print(0)
        return
    a = []
    b = []
    for i in range(n):
        if s1[i] == '1':
            a.append(i)
        if s2[i] == '1':
            b.append(i)
    if len(a) != len(b):
        print(0)
        return
    ans = 1
    for i in range(len(a)):
        ans = ans * C(len(a) - 1, i) % mod
    ans = ans * ans % mod
    for i in range(len(a)):
        ans = ans * C(len(a) - 1, i) % mod
    print(ans)

T = 1
for _ in range(T):
    solve()
