import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());

        long N = n;
        long M = m;
        long Np = 1;
        long Mp = 1;
        long Nq = 1;
        long Mq = 1;

        while (N > 0) {
            if (N % 2 == 1) {
                Np = (Np * M) % MOD;
                Nq = (Nq * Mq) % MOD;
            }
            Mq = (Mq * M) % MOD;
            M = (M + 1) % MOD;
            N /= 2;
        }

        while (M > 0) {
            if (M % 2 == 1) {
                Mp = (Mp * N) % MOD;
                Mq = (Mq * Nq) % MOD;
            }
            Nq = (Nq * N) % MOD;
            N = (N + 1) % MOD;
            M /= 2;
        }

        long Q = (Np + Mp) % MOD;
        long Qinv = (long) (pow(Q, MOD - 2, MOD)) % MOD;
        long R = (Np * Qinv) % MOD;

        System.out.println(R);
    }

    static long pow(long a, long b, long mod) {
        long res = 1;
        a %= mod;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % mod;
            }
            a = (a * a) % mod;
            b >>= 1;
        }
        return res;
    }
}
