#include <iostream>
#include <string>
#include <unordered_set>

int main() {
    std::string s;
    std::cin >> s;

    std::unordered_set<char> set(s.begin(), s.end());
    for (char c = 'a'; c <= 'z'; ++c) {
        if (set.find(c) == set.end()) {
            std::cout << c << std::endl;
            return 0;
        }
    }
    std::cout << "None" << std::endl;

    return 0;
}
