import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }
        Arrays.sort(a);
        long maxArea = 0;
        for (int i = n - 1; i >= 3; i--) {
            for (int j = i - 1; j >= 2; j--) {
                long area = (long) a[i] * a[j];
                if (a[i - 1] == a[j - 1]) {
                    area *= a[i - 2];
                } else if (a[i - 1] == a[j - 2]) {
                    area *= a[i - 2];
                } else if (a[i - 2] == a[j - 2]) {
                    area *= a[i - 1];
                } else {
                    continue;
                }
                maxArea = Math.max(maxArea, area);
            }
        }
        System.out.println(maxArea);
    }
}
