#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXN 6000
#define MAXM 6000

typedef struct {
    int len;
    int bend;
} Bar;

int cmp(const void *a, const void *b) {
    Bar *x = (Bar *)a;
    Bar *y = (Bar *)b;
    return x->len - y->len;
}

int cmp_bend(const void *a, const void *b) {
    Bar *x = (Bar *)a;
    Bar *y = (Bar *)b;
    return x->bend - y->bend;
}

int main() {
    int N, M, X, Y;
    scanf("%d %d %d %d", &N, &M, &X, &Y);
    Y = 12 - X;
    Bar *a = (Bar *)malloc(N * sizeof(Bar));
    Bar *b = (Bar *)malloc(M * sizeof(Bar));
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i].len);
        a[i].bend = 0;
    }
    for (int i = 0; i < M; i++) {
        scanf("%d", &b[i].bend);
        b[i].len = 0;
    }
    qsort(a, N, sizeof(Bar), cmp);
    qsort(b, M, sizeof(Bar), cmp_bend);
    int count = 0;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            for (int k = j + 1; k < N; k++) {
                if (a[i].len == a[j].len && a[j].len == a[k].len) {
                    count++;
                }
                if (a[i].len == a[j].len && a[j].len != a[k].len) {
                    int l = 0;
                    while (l < M && b[l].bend < a[i].len) {
                        l++;
                    }
                    if (l < M && b[l].bend + b[l].len == a[k].len) {
                        count++;
                    }
                }
                if (a[i].len != a[j].len && a[j].len == a[k].len) {
                    int l = 0;
                    while (l < M && b[l].bend < a[j].len) {
                        l++;
                    }
                    if (l < M && b[l].bend + b[l].len == a[i].len) {
                        count++;
                    }
                }
                if (a[i].len != a[j].len && a[j].len != a[k].len) {
                    int l = 0;
                    while (l < M && b[l].bend < a[i].len) {
                        l++;
                    }
                    int m = 0;
                    while (m < M && b[m].bend < a[j].len) {
                        m++;
                    }
                    if (l < M && m < M && b[l].bend + b[l].len == a[j].len && b[m].bend + b[m].len == a[i].len) {
                        count++;
                    }
                }
            }
        }
    }
    printf("%d\n", count);
    free(a);
    free(b);
    return 0;
}
