#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007

int dp[55][55][3][3];
char s1[105], s2[105];

int dfs(int i, int j, int c1, int c2) {
    if (i == j) return 1;
    if (dp[i][j][c1][c2] != -1) return dp[i][j][c1][c2];
    int res = 0;
    if (c1 == 0) {
        if (c2 == 0) {
            res = (res + dfs(i + 1, j, 1, 0)) % MOD;
            res = (res + dfs(i, j + 1, 0, 1)) % MOD;
        } else {
            res = (res + dfs(i + 1, j, 1, 0)) % MOD;
        }
    } else if (c2 == 0) {
        res = (res + dfs(i, j + 1, 0, 1)) % MOD;
    } else {
        res = (res + dfs(i + 1, j, 1, 0)) % MOD;
        res = (res + dfs(i, j + 1, 0, 1)) % MOD;
    }
    dp[i][j][c1][c2] = res;
    return res;
}

int main() {
    int n;
    scanf("%d %s %s", &n, s1, s2);
    memset(dp, -1, sizeof dp);
    int res = 0;
    for (int i = 0; i < n; i++) {
        if (s1[i] == s2[i]) {
            res = (res + dfs(i + 1, i + 2, 0, 0)) % MOD;
        } else {
            res = (res + dfs(i + 1, i + 2, 1, 1)) % MOD;
        }
    }
    printf("%d\n", res);
    return 0;
}
