MOD = 1000000007

def dfs(i, j, color):
    if i == n:
        return 1
    if dp[i][j][color] != -1:
        return dp[i][j][color]
    ans = 0
    for di, dj in [(0, 1), (1, 0)]:
        ni, nj = i + di, j + dj
        if 0 <= ni < n and 0 <= nj < n and (ni, nj) != (i, j):
            if color == 0:
                ans += dfs(ni, nj, 1)
                ans += dfs(ni, nj, 2)
            else:
                if (ni, nj) == (i + 1, j):
                    ans += dfs(ni, nj, 2)
                else:
                    ans += dfs(ni, nj, 1)
    dp[i][j][color] = ans % MOD
    return ans % MOD

def solve():
    global n, dp
    n = int(input())
    s1 = input()
