    ans = 0
    for i in range(n):
        for j in range(n):
            if i == 0 and j == 0:
                ans += dfs(i, j, 0)
            elif i == 0:
                ans += dfs(i, j, 1)
            elif j == 0:
                ans += dfs(i, j, 2)
    print(ans % MOD)

