#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100005

int main() {
    int n;
    scanf("%d", &n);

    int a[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }

    qsort(a, n, sizeof(int), compare);

    long long max_area = 0;
    for (int i = 0; i < n - 3; i++) {
        for (int j = i + 1; j < n - 2; j++) {
            for (int k = j + 1; k < n - 1; k++) {
                for (int l = k + 1; l < n; l++) {
                    long long area = (long long)a[i] * a[j] * a[k] * a[l];
                    max_area = (max_area > area) ? max_area : area;
                }
            }
        }
    }

    printf("%lld\n", max_area);

    return 0;
}

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}
