#include <bits/stdc++.h>
using namespace std;

int main() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    sort(a.begin(), a.end());
    long long ans = 0;
    for (int i = 0; i < n - 3; i++) {
        for (int j = i + 1; j < n - 2; j++) {
            for (int k = j + 1; k < n - 1; k++) {
                for (int l = k + 1; l < n; l++) {
                    long long area = 1LL * a[i] * a[j] * a[k] * a[l];
                    ans = max(ans, area);
                }
            }
        }
    }
    for (int i = 0; i < n - 3; i++) {
        long long area = 1LL * a[i] * a[i + 1] * a[i + 2] * a[i + 3];
        ans = max(ans, area);
    }
    cout << ans << endl;
    return 0;
}
