#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007
#define MAXN 55

int dp[2][MAXN][MAXN][3][3];
char s1[MAXN], s2[MAXN];

int dfs(int i, int j, int c1, int c2) {
    if (i == 0 && j == 0) return 1;
    int &ret = dp[c1][c2][i][j][0];
    if (ret != -1) return ret;
    ret = 0;
    if (i > 0 && s1[i - 1] == s1[i - 2]) {
        if (c1 == 0 || c1 != c2) {
            ret = (ret + dfs(i - 2, j, c1, c2)) % MOD;
        }
        if (c2 == 0 || c2 != c1) {
            ret = (ret + dfs(i - 2, j, c1, c2)) % MOD;
        }
    }
    if (j > 0 && s2[j - 1] == s2[j - 2]) {
        if (c1 == 0 || c1 != c2) {
            ret = (ret + dfs(i, j - 2, c1, c2)) % MOD;
        }
        if (c2 == 0 || c2 != c1) {
            ret = (ret + dfs(i, j - 2, c1, c2)) % MOD;
        }
    }
    return ret;
}

int main() {
    int n;
    scanf("%d", &n);
    scanf("%s", s1);
    scanf("%s", s2);
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k < 3; k++) {
                for (int l = 0; l < 3; l++) {
                    dp[k][l][i][j][0] = -1;
                    dp[k][l][i][j][1] = -1;
                    dp[k][l][i][j][2] = -1;
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            ans = (ans + dfs(n, n, i, j)) % MOD;
        }
    }
    printf("%d\n", ans);
    return 0;
}
