#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MOD = 1000000007;

int main() {
    int n;
    cin >> n;
    string s1, s2;
    cin >> s1 >> s2;

    vector<vector<int>> dp(n, vector<int>(4, 0));
    dp[0][0] = 1;

    for (int i = 1; i < n; i++) {
        if (s1[i] == s1[i-1] || s2[i] == s2[i-1]) {
            dp[i][0] = (dp[i-1][0] + dp[i-1][1] + dp[i-1][2]) % MOD;
            dp[i][1] = (dp[i-1][0] + dp[i-1][2]) % MOD;
            dp[i][2] = (dp[i-1][0] + dp[i-1][1]) % MOD;
            dp[i][3] = dp[i-1][1] + dp[i-1][2];
        } else {
            dp[i][0] = (dp[i-1][0] + dp[i-1][1] + dp[i-1][2]) % MOD;
            dp[i][1] = (dp[i-1][0] + dp[i-1][2]) % MOD;
            dp[i][2] = (dp[i-1][0] + dp[i-1][1]) % MOD;
            dp[i][3] = (dp[i-1][0] + dp[i-1][1] + dp[i-1][2]) % MOD;
        }
    }

    int ans = (dp[n-1][0] + dp[n-1][1] + dp[n-1][2] + dp[n-1][3]) % MOD;

    cout << ans << endl;

    return 0;
}
