MOD = 1000000007

def dfs(i, j, color):
    if i == n:
        return 1
    if dp[i][j][color] != -1:
        return dp[i][j][color]
    dp[i][j][color] = 0
    if i + 1 < n and j + 1 < n and S1[i][j] == S1[i + 1][j] == S1[i][j + 1] == S1[i + 1][j + 1]:
        for c in range(3):
            if c != color:
                dp[i][j][color] += dfs(i + 2, j + 2, c)
                dp[i][j][color] %= MOD
    elif i + 1 < n and S1[i][j] == S1[i + 1][j]:
        for c in range(3):
            if c != color:
                dp[i][j][color] += dfs(i + 2, j, c)
                dp[i][j][color] %= MOD
    elif j + 1 < n and S1[i][j] == S1[i][j + 1]:
        for c in range(3):
            if c != color:
                dp[i][j][color] += dfs(i, j + 2, c)
                dp[i][j][color] %= MOD
    else:
        for c in range(3):
            if c != color:
                dp[i][j][color] += dfs(i + 1, j + 1, c)
                dp[i][j][color] %= MOD
    return dp[i][j][color]

def solve():
    global n, S1, S2, dp
    n = int(input())
    S1 = input()
    S2 = input()
    dp = [[[-1] * 3 for _ in range(n)] for _ in range(n)]
    ans = 0
    for i in range(n):
        for j in range(n):
            for c in range(3):
                ans += dfs(i, j, c)
                ans %= MOD
    print(ans)

solve()
