#include <iostream>
#include <string>
#include <unordered_map>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    string s;
    cin >> s;

    int n = s.size();
    vector<int> cnt(26, 0);
    for (char c : s) {
        cnt[c - 'a']++;
    }

    string res = "";
    for (int i = 0; i < 26; i++) {
        if (cnt[i] == 0) {
            res = char('a' + i);
            break;
        }
    }

    if (res.empty()) {
        for (int i = 0; i < 26; i++) {
            if (cnt[i] == 1) {
                res = char('a' + i);
                break;
            }
        }
    }

    if (res.empty()) {
        int minLen = n + 1;
        string ans = "";
        for (int i = 0; i < 26; i++) {
            string t = "";
            for (int j = 0; j < 26; j++) {
                if (i != j) {
                    t += char('a' + j);
                }
            }
            if (t.size() < minLen) {
                minLen = t.size();
                ans = t;
            } else if (t.size() == minLen && t < ans) {
                ans = t;
            }
        }
        res = ans;
    }

    cout << res << endl;

    return 0;
}
