import java.io.*;
import java.util.*;

public class Main {
    static int N = (int) 2e5 + 5;
    static int[] a = new int[N];
    static int[] vis = new int[26];
    static int[] nxt = new int[26];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        String s = br.readLine();
        int n = s.length();
        for (int i = 0; i < n; i++) {
            a[i] = s.charAt(i) - 'a';
            vis[a[i]] = 1;
        }
        Arrays.sort(a, 0, n);
        int p = 0;
        for (int i = 0; i < 26; i++) {
            if (vis[i] == 0) {
                nxt[i] = p;
                p++;
            } else {
                nxt[i] = nxt[a[p - 1]];
            }
        }
        int ans = Integer.MAX_VALUE;
        int pos = -1;
        for (int i = 0; i < 26; i++) {
            if (vis[i] == 0) {
                int len = 0;
                int j = i;
                while (j != -1) {
                    len++;
                    j = nxt[j];
                }
                if (ans > len) {
                    ans = len;
                    pos = i;
                } else if (ans == len) {
                    pos = Math.min(pos, i);
                }
            }
        }
        for (int i = 0; i < ans; i++) {
            pw.print((char) (pos + 'a'));
        }
        pw.println();
        pw.close();
    }
}
