#include <stdio.h>
#include <string.h>

int max(int a, int b) {
    return (a > b) ? a : b;
}

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    int max_black_row = 0;
    int max_black_col = 0;
    int black_row[H];
    int black_col[W];
    for (int i = 0; i < H; i++) {
        char str[W + 1];
        scanf("%s", str);
        int black = 0;
        for (int j = 0; j < W; j++) {
            if (str[j] == '#') {
                black++;
            }
        }
        black_row[i] = black;
        max_black_row = max(max_black_row, black);
    }
    for (int j = 0; j < W; j++) {
        int black = 0;
        for (int i = 0; i < H; i++) {
            if (black_row[i] != 0) {
                black++;
            }
        }
        black_col[j] = black;
        max_black_col = max(max_black_col, black);
    }
    int max_area = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int area = black_row[i] * black_col[j];
            max_area = max(max_area, area);
            if (black_row[i] != 0 && black_col[j] != 0) {
                black_row[i]--;
                black_col[j]--;
                area = black_row[i] * black_col[j];
                max_area = max(max_area, area);
                black_row[i]++;
                black_col[j]++;
            }
        }
    }
    printf("%d\n", max_area);
    return 0;
}
