#include <iostream>
#include <vector>
#include <algorithm>
#include <string>

using namespace std;

int main() {
    int H, W;
    cin >> H >> W;

    vector<string> S(H);
    for (int i = 0; i < H; i++) {
        cin >> S[i];
    }

    vector<int> maxBlackRow(H, 0);
    vector<int> maxBlackCol(W, 0);

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (S[i][j] == '#') {
                maxBlackRow[i]++;
                maxBlackCol[j]++;
            }
        }
    }

    int maxArea = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int area = maxBlackRow[i] * maxBlackCol[j];
            maxArea = max(maxArea, area);
        }
    }

    cout << maxArea << endl;

    return 0;
}
