import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int W = scanner.nextInt();
        String[] S = new String[H];
        for (int i = 0; i < H; i++) {
            S[i] = scanner.next();
        }
        int[] row = new int[H];
        int[] col = new int[W];
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (S[i].charAt(j) == '#') {
                    row[i]++;
                    col[j]++;
                }
            }
        }
        int maxArea = 0;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                int area = row[i] * col[j];
                maxArea = Math.max(maxArea, area);
            }
        }
        for (int i = 0; i < H; i++) {
            for (int j = i + 1; j < H; j++) {
                int intersection = 0;
                for (int k = 0; k < W; k++) {
                    if (S[i].charAt(k) == S[j].charAt(k)) {
                        intersection++;
                    }
                }
                int area = row[i] * (col[j] - intersection) + row[j] * (col[i] - intersection) + intersection * Math.min(row[i], row[j]);
                maxArea = Math.max(maxArea, area);
            }
        }
        System.out.println(maxArea);
    }
}
