import sys

def solve():
    H, W = map(int, sys.stdin.readline().split())
    S = [sys.stdin.readline().rstrip() for _ in range(H)]

    max_black = [0] * H
    max_white = [0] * H
    max_black_col = [0] * W
    max_white_col = [0] * W

    for i in range(H):
        for j in range(W):
            if S[i][j] == '#':
                max_black[i] = max(max_black[i], j + 1)
                max_black_col[j] = max(max_black_col[j], i + 1)
            else:
                max_white[i] = max(max_white[i], j + 1)
                max_white_col[j] = max(max_white_col[j], i + 1)

    ans = 0
    for i in range(H):
        for j in range(W):
            ans = max(ans, max_black[i] * max_black_col[j])
            ans = max(ans, max_white[i] * max_white_col[j])

    print(ans)

if __name__ == '__main__':
    solve()
