#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 100005
#define M 100005

typedef struct {
    int to, next, w;
} Edge;

Edge e[M << 1];
int head[N], cnt = 1;

void add(int u, int v, int w) {
    e[++cnt] = (Edge) {v, head[u], w};
    head[u] = cnt;
}

int dep[N], fa[N][20], dis[N][20];

void dfs(int u, int f) {
    fa[u][0] = f;
    dep[u] = dep[f] + 1;
    dis[u][0] = e[head[u] >> 1].w;
    for (int i = 1; i <= 19; i++) {
        fa[u][i] = fa[fa[u][i - 1]][i - 1];
        dis[u][i] = dis[u][i - 1] + dis[fa[u][i - 1]][i - 1];
    }
    for (int i = head[u]; i; i = e[i].next) {
        int v = e[i].to;
        if (v == f) continue;
        dfs(v, u);
    }
}

int lca(int u, int v) {
    if (dep[u] < dep[v]) {
        int temp = u;
        u = v;
        v = temp;
    }
    for (int i = 19; i >= 0; i--) {
        if (dep[fa[u][i]] >= dep[v]) {
            u = fa[u][i];
        }
    }
    if (u == v) return u;
    for (int i = 19; i >= 0; i--) {
        if (fa[u][i] != fa[v][i]) {
            u = fa[u][i];
            v = fa[v][i];
        }
    }
    return fa[u][0];
}

int main() {
    int n, q, k;
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        add(u, v, w);
        add(v, u, w);
    }
    scanf("%d%d", &q, &k);
    dfs(k, 0);
    for (int i = 1; i <= q; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        int l = lca(u, v);
        printf("%d\n", dis[u][0] + dis[v][0] - 2 * dis[l][0]);
    }
    return 0;
}
