#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 1e5 + 5;

struct Edge {
    int to, weight;
};

vector<Edge> adj[MAX_N];
int dist[MAX_N][20];
int depth[MAX_N];

void dfs(int u, int p, int d) {
    depth[u] = d;
    dist[u][0] = p == -1 ? 0 : adj[p][0].weight;
    for (int i = 1; i < 20; i++) {
        dist[u][i] = dist[dist[u][i - 1]][i - 1];
    }
    for (const auto& e : adj[u]) {
        if (e.to != p) {
            dfs(e.to, u, d + 1);
        }
    }
}

int lca(int u, int v) {
    if (depth[u] < depth[v]) {
        swap(u, v);
    }
    for (int i = 19; i >= 0; i--) {
        if (depth[u] - (1 << i) >= depth[v]) {
            u = dist[u][i];
        }
    }
    if (u == v) {
        return u;
    }
    for (int i = 19; i >= 0; i--) {
        if (dist[u][i] != dist[v][i]) {
            u = dist[u][i];
            v = dist[v][i];
        }
    }
    return dist[u][0];
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        adj[u].push_back({v, w});
        adj[v].push_back({u, w});
    }
    int q, k;
    cin >> q >> k;
    dfs(k, -1, 0);
    for (int i = 1; i <= q; i++) {
        int u, v;
        cin >> u >> v;
        int l = lca(u, v);
        cout << dist[u][0] + dist[v][0] - 2 * dist[l][0] << '\n';
    }

    return 0;
}
