#include <stdio.h>
#include <stdlib.h>

#define MAX_H 100
#define MAX_W 100
#define MAX_N 100000

int main() {
    int h, w, n;
    scanf("%d %d %d", &h, &w, &n);
    int a[MAX_N + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    int x[MAX_H * MAX_W + 1], y[MAX_H * MAX_W + 1];
    int cnt = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= a[i]; j++) {
            x[++cnt] = (i - 1) * w + j;
            y[cnt] = i;
        }
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            int p = (i - 1) * w + j;
            if (p <= cnt) {
                printf("%d ", y[p]);
            } else {
                printf("0 ");
            }
        }
        printf("\n");
    }
    return 0;
}
