#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_H = 100;
const int MAX_W = 100;
const int MAX_N = 10000;

int H, W, N;
vector<int> A;
vector<vector<int>> grid;

void paint(int h, int w, int color) {
    grid[h][w] = color;
    queue<pair<int, int>> q;
    q.push({h, w});
    while (!q.empty()) {
        int x = q.front().first;
        int y = q.front().second;
        q.pop();
        for (int dx = -1; dx <= 1; dx++) {
            for (int dy = -1; dy <= 1; dy++) {
                if (dx == 0 && dy == 0) continue;
                int nx = x + dx;
                int ny = y + dy;
                if (nx < 0 || nx >= H || ny < 0 || ny >= W) continue;
                if (grid[nx][ny] == 0) {
                    grid[nx][ny] = color;
                    q.push({nx, ny});
                }
            }
        }
    }
}

int main() {
    cin >> H >> W;
    cin >> N;
    A.resize(N);
    for (int i = 0; i < N; i++) {
        cin >> A[i];
    }
    grid.resize(H, vector<int>(W, 0));
    int color = 1;
    int idx = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (idx < N && A[idx] > 0) {
                paint(i, j, color);
                A[idx]--;
                idx++;
            }
            color++;
        }
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            cout << grid[i][j] << " ";
        }
        cout << endl;
    }
    return 0;
}
