import sys

def solve():
    H, W = map(int, input().split())
    N = int(input())
    a = list(map(int, input().split()))

    grid = [[0] * W for _ in range(H)]
    color = 1
    for i in range(N):
        for _ in range(a[i]):
            x, y = 0, 0
            while True:
                grid[x][y] = color
                if x > 0 and grid[x-1][y] == 0:
                    x -= 1
                elif y > 0 and grid[x][y-1] == 0:
                    y -= 1
                else:
                    if x < H - 1 and grid[x+1][y] == 0:
                        x += 1
                    elif y < W - 1 and grid[x][y+1] == 0:
                        y += 1
                    else:
                        break
            color += 1

    for row in grid:
        print(*row)

if __name__ == "__main__":
    solve()
