#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100
#define MAX 10000000

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return 0;
    return 1;
}

int min(int a, int b) {
    return a < b ? a : b;
}

int main() {
    int n, x[N], ans = 0;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &x[i]);
    }
    for (int i = 0; i < n; i++) {
        int p = x[i];
        while (p <= MAX) {
            if (is_prime(p)) {
                int cnt = 0;
                for (int j = i; j < n; j++) {
                    if (x[j] >= p) {
                        cnt++;
                    } else {
                        break;
                    }
                }
                ans = min(ans, cnt + (p - 2) / 2);
                p = p + p - 1;
            } else {
                p++;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
