#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

int main() {
    int N;
    cin >> N;
    vector<int> A(N);
    for (int i = 0; i < N; i++) {
        cin >> A[i];
    }

    int ans = 0;
    for (int p = 3; p <= 10000000; p++) {
        if (p % 2 == 0) continue;
        if (p == 3) {
            for (int i = 0; i < N; i++) {
                if (A[i] % p == 0) {
                    ans++;
                    break;
                }
            }
        } else {
            int cnt = 0;
            for (int i = 0; i < N; i++) {
                if (A[i] % p == 0) {
                    cnt++;
                } else {
                    break;
                }
            }
            if (cnt > 0) {
                ans += (cnt + p - 1) / p;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
