import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static final int N = 10000000;
    static final int M = 10000000;
    static final int INF = 0x3f3f3f3f;

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);
        int n = Integer.parseInt(in.readLine());
        StringTokenizer st = new StringTokenizer(in.readLine());
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            b[i] = a[i];
        }
        Arrays.sort(b);
        int[] c = new int[n];
        int p = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || b[i] != b[i - 1] + 1) {
                c[p++] = b[i];
            }
        }
        int[] d = new int[p];
        for (int i = 0; i < p; i++) {
            d[i] = c[i];
        }
        int[] e = new int[p];
        for (int i = 0; i < p; i++) {
            e[i] = 1;
        }
        int[] f = new int[p];
        for (int i = 0; i < p; i++) {
            f[i] = 1;
        }
        for (int i = 0; i < p; i++) {
            for (int j = 1; j * j <= d[i]; j++) {
                if (d[i] % j == 0) {
                    int k = d[i] / j;
                    if (j >= 3 && k >= 3) {
                        e[i] = Math.max(e[i], j + k);
                        f[i] = Math.max(f[i], j);
                    }
                    if (j != k && k >= 3 && j >= 3) {
                        e[i] = Math.max(e[i], j + k);
                        f[i] = Math.max(f[i], k);
                    }
                }
            }
        }
        int[] g = new int[p];
        for (int i = 0; i < p; i++) {
            g[i] = e[i] + f[i];
        }
        int[] h = new int[p];
        for (int i = 0; i < p; i++) {
            h[i] = g[i];
        }
        Arrays.sort(h);
        int ans = 0;
        for (int i = 0; i < p; i++) {
            int j = Arrays.binarySearch(h, g[i]);
            if (j < 0) {
                j = -j - 1;
            }
            ans += j;
        }
        out.println(ans);
        out.close();
    }
}
