import math

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def solve():
    n = int(input())
    nums = list(map(int, input().split()))
    nums.sort()
    ans = 0
    for i in range(n):
        if i == 0:
            p = nums[i]
        else:
            p = math.gcd(p, nums[i] - nums[i - 1])
        while p >= 3 and is_prime(p):
            ans += 1
            j = nums[i] - nums[i - 1]
            while j >= p:
                j -= p
            nums[i] = nums[i] - j
            p = math.gcd(p, nums[i] - nums[i - 1])
    print(ans + n)

if __name__ == "__main__":
    solve()
