#include <iostream>
#include <vector>
#include <queue>
#include <unordered_map>

using namespace std;

const int MAX_N = 200000;

int main() {
    int n, m;
    cin >> n >> m;

    vector<int> adj[MAX_N + 1];
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
    }

    queue<int> q;
    q.push(1);
    vector<bool> visited(n + 1, false);
    visited[1] = true;

    int step = 1;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; i++) {
            int u = q.front();
            q.pop();
            for (int v : adj[u]) {
                if (v == n) {
                    cout << step + 1 << "POSSIBLE" << endl;
                    return 0;
                }
                if (!visited[v]) {
                    visited[v] = true;
                    q.push(v);
                }
            }
        }
        step++;
    }

    cout << "IMPOSSIBLE" << endl;
    return 0;
}
