import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        long k = scanner.nextLong();
        int n = 2;
        while (n * (n - 1) / 2 < k) {
            n++;
        }
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = n - 1;
        }
        long t = k - (n - 1) * (n - 2) / 2;
        for (int i = 0; i < n; i++) {
            if (t >= a[i]) {
                a[i] -= n;
                t -= a[i];
                a[i] = 0;
            } else {
                a[i] -= t;
                t = 0;
            }
        }
        System.out.println(n);
        for (int i = 0; i < n; i++) {
            System.out.print(a[i] + " ");
        }
    }
}
