import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextLong();
        }
        long ans = 0;
        while (true) {
            long max = 0;
            for (int i = 0; i < n; i++) {
                if (a[i] > max) {
                    max = a[i];
                }
            }
            if (max == n - 1) {
                break;
            }
            long sum = 0;
            for (int i = 0; i < n; i++) {
                sum += a[i];
            }
            sum -= max;
            sum += n * max;
            ans += sum;
            for (int i = 0; i < n; i++) {
                if (i != max) {
                    a[i]++;
                } else {
                    a[i] -= n;
                }
            }
        }
        System.out.println(ans);
    }
}
