#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n;
int p[MAXN];
int in[MAXN];
int out[MAXN];
int deg[MAXN];
int vis[MAXN];
int color[MAXN];

void dfs(int u) {
    vis[u] = 1;
    for (int v = out[u]; v; v = out[v]) {
        if (!vis[p[v]]) {
            dfs(p[v]);
        }
    }
    for (int v = in[u]; v; v = in[v]) {
        if (color[p[v]] == color[u]) {
            printf("IMPOSSIBLE\n");
            exit(0);
        }
    }
    color[u] = 1;
    for (int v = out[u]; v; v = out[v]) {
        if (color[p[v]] == 0) {
            color[p[v]] = color[u] ^ 1;
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &p[i]);
        in[p[i]] = i;
        out[i] = p[i];
        deg[p[i]]++;
    }
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            dfs(i);
        }
    }
    printf("POSSIBLE\n");
    return 0;
}
