#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 200005;

int n, p[N], deg[N], cnt;
vector<int> g[N];
queue<int> q;

void dfs(int u) {
    for (int v : g[u]) {
        if (!deg[v]) {
            deg[v] = u;
            dfs(v);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> p[i];
        g[p[i]].push_back(i);
    }

    for (int i = 1; i <= n; i++) {
        if (!deg[i]) {
            dfs(i);
        }
    }

    for (int i = 1; i <= n; i++) {
        if (deg[i] == i) {
            cout << "IMPOSSIBLE\n";
            return 0;
        }
    }

    for (int i = 1; i <= n; i++) {
        if (deg[i] == p[i]) {
            cout << "IMPOSSIBLE\n";
            return 0;
        }
    }

    for (int i = 1; i <= n; i++) {
        if (deg[i] != i && deg[i] != p[i]) {
            cout << "IMPOSSIBLE\n";
            return 0;
        }
    }

    cout << "POSSIBLE\n";

    return 0;
}
