#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    sort(a.begin(), a.end());

    if (binary_search(a.begin(), a.end(), k)) {
        cout << "POSSIBLE" << endl;
        return 0;
    }

    for (int i = 0; i < n - 1; i++) {
        int diff = abs(a[i] - a[i + 1]);
        auto it = lower_bound(a.begin(), a.end(), diff);
        if (it != a.end() && *it == diff) {
            cout << "POSSIBLE" << endl;
            return 0;
        }
        if (it != a.begin() && *(it - 1) == diff) {
            cout << "POSSIBLE" << endl;
            return 0;
        }
        a.push_back(diff);
        sort(a.begin(), a.end());
    }

    cout << "IMPOSSIBLE" << endl;

    return 0;
}
