import java.util.*;
import java.io.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        long k = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        Arrays.sort(a);
        for (int i = 0; i < n - 1; i++) {
            if (Math.abs(a[i] - a[i + 1]) == k) {
                System.out.println("POSSIBLE");
                return;
            }
            if (Math.abs(a[i] - a[i + 1]) > k) {
                System.out.println("IMPOSSIBLE");
                return;
            }
        }
        if (a[0] == k || a[n - 1] == k) {
            System.out.println("POSSIBLE");
            return;
        }
        for (int i = 0; i < n - 1; i++) {
            if (a[i] % 2 == 0 && a[i + 1] % 2 == 0) {
                if (k % 2 == 0 && k / 2 <= a[i] / 2 && k / 2 <= a[i + 1] / 2) {
                    System.out.println("POSSIBLE");
                    return;
                }
            } else if (a[i] % 2 == 1 && a[i + 1] % 2 == 1) {
                if (k % 2 == 1 && k / 2 <= (a[i] + a[i + 1]) / 2) {
                    System.out.println("POSSIBLE");
                    return;
                }
            } else {
                if (k % 2 == 0 && k / 2 <= a[i] / 2 && k / 2 <= (a[i + 1] + 1) / 2) {
                    System.out.println("POSSIBLE");
                    return;
                } else if (k % 2 == 1 && k / 2 <= (a[i] + 1) / 2 && k / 2 <= a[i + 1] / 2) {
                    System.out.println("POSSIBLE");
                    return;
                }
            }
        }
        System.out.println("IMPOSSIBLE");
    }
}
