#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 300
#define MAX_M 300

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int a[MAX_N][MAX_M];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            scanf("%d", &a[i][j]);
            a[i][j]--;
        }
    }

    int count[MAX_M] = {0};
    for (int i = 0; i < n; i++) {
        count[a[i][0]]++;
    }

    int max_count = 0;
    for (int i = 0; i < m; i++) {
        max_count = (count[i] > max_count) ? count[i] : max_count;
    }

    int min_max_count = max_count;
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < n; j++) {
            if (a[j][0] != i) {
                count[i]--;
                count[a[j][0]]++;
                max_count = (count[i] > max_count) ? count[i] : max_count;
                min_max_count = (max_count < min_max_count) ? max_count : min_max_count;
                count[i]++;
                count[a[j][0]]--;
            }
        }
    }

    printf("%d\n", min_max_count + 1);

    return 0;
}
