#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> a(n, vector<int>(m));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            cin >> a[i][j];
            a[i][j]--;
        }
    }

    vector<int> cnt(m);
    for (int i = 0; i < n; i++) {
        cnt[a[i][0]]++;
    }

    int ans = n;
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < n; j++) {
            if (a[j][0] != i) {
                cnt[a[j][0]]--;
                cnt[i]++;
                ans = min(ans, *max_element(cnt.begin(), cnt.end()));
                cnt[a[j][0]]++;
                cnt[i]--;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
