#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int gold;
    int silver;
    int bronze;
} Person;

int compare(const void *a, const void *b) {
    Person *p1 = (Person *)a;
    Person *p2 = (Person *)b;
    if (p1->gold > p2->gold) return -1;
    if (p1->gold < p2->gold) return 1;
    if (p1->silver > p2->silver) return -1;
    if (p1->silver < p2->silver) return 1;
    if (p1->bronze > p2->bronze) return -1;
    if (p1->bronze < p2->bronze) return 1;
    return 0;
}

int main() {
    int X, Y, Z;
    scanf("%d %d %d", &X, &Y, &Z);
    Person people[X + Y + Z];
    for (int i = 0; i < X + Y + Z; i++) {
        scanf("%d %d %d", &people[i].gold, &people[i].silver, &people[i].bronze);
    }
    qsort(people, X + Y + Z, sizeof(Person), compare);
    long long total = 0;
    for (int i = 0; i < X; i++) {
        total += people[i].gold;
    }
    for (int i = X; i < X + Y; i++) {
        total += people[i].silver;
    }
    for (int i = X + Y; i < X + Y + Z; i++) {
        total += people[i].bronze;
    }
    printf("%lld\n", total);
    return 0;
}
