#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int x, y, z;
    cin >> x >> y >> z;

    vector<pair<int, int>> gold(x);
    vector<pair<int, int>> silver(y);
    vector<pair<int, int>> bronze(z);

    for (int i = 0; i < x; i++) {
        int a, b;
        cin >> a >> b;
        gold[i] = {a, b};
    }

    for (int i = 0; i < y; i++) {
        int a, b;
        cin >> a >> b;
        silver[i] = {a, b};
    }

    for (int i = 0; i < z; i++) {
        int a, b;
        cin >> a >> b;
        bronze[i] = {a, b};
    }

    sort(gold.begin(), gold.end());
    sort(silver.begin(), silver.end());
    sort(bronze.begin(), bronze.end());

    long long total = 0;
    int goldIndex = x - 1, silverIndex = y - 1, bronzeIndex = z - 1;

    while (goldIndex >= 0 && silverIndex >= 0 && bronzeIndex >= 0) {
        if (gold[goldIndex].second > silver[silverIndex].second && gold[goldIndex].second > bronze[bronzeIndex].second) {
            total += gold[goldIndex].first;
            goldIndex--;
        } else if (silver[silverIndex].second > bronze[bronzeIndex].second) {
            total += silver[silverIndex].first;
            silverIndex--;
        } else {
            total += bronze[bronzeIndex].first;
            bronzeIndex--;
        }
    }

    cout << total << endl;

    return 0;
}
