#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define N 100005
#define INF INT_MAX / 2

int n;
int a[N], b[N], c[N];
int d[N], p[N], f[N];

void dfs(int u, int fa) {
    for (int i = 1; i <= n; i++) {
        d[i] = INF;
    }
    d[u] = 0;
    for (int i = 1; i <= n; i++) {
        int v;
        for (int j = 1; j <= n; j++) {
            if (j == u) continue;
            if (d[j] < d[v] || d[v] == INF) {
                v = j;
            }
        }
        p[v] = u;
        f[v] = d[v];
        d[v] = INF;
        for (int j = 1; j <= n; j++) {
            if (j == u || j == v) continue;
            if (c[j] < d[v] || d[v] == INF) {
                d[v] = c[j];
            }
        }
        u = v;
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d %d %d", &a[i], &b[i], &c[i]);
    }
    for (int i = 1; i <= n; i++) {
        if (a[i] > b[i]) {
            int temp = a[i];
            a[i] = b[i];
            b[i] = temp;
        }
    }
    for (int i = 1; i <= n; i++) {
        if (a[i] == 1) {
            dfs(1, 0);
            break;
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = ans + f[i];
    }
    printf("%d\n", ans);
    return 0;
}
