#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n;
vector<int> g[N];
int d[N];
int p[N];

void dfs(int u, int par) {
    for (int v : g[u]) {
        if (v == par) continue;
        dfs(v, u);
        p[u] = max(p[u], p[v] + d[u] - d[v]);
    }
    p[u] = max(p[u], n - 1 - p[u]);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        g[u].push_back(v);
        g[v].push_back(u);
        d[u] = w;
        d[v] = w;
    }

    dfs(1, 0);
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = max(ans, p[i]);
    }

    cout << ans << '\n';

    return 0;
}
