import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[][] G;
    static int[][] D;
    static int[] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        N = Integer.parseInt(br.readLine());
        G = new int[N + 1][2];
        D = new int[N + 1][2];
        dp = new int[N + 1];

        for (int i = 1; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            int w = Integer.parseInt(st.nextToken());
            G[i][0] = u;
            G[i][1] = v;
            D[u][0] = Math.min(D[u][0], w);
            D[u][1] = Math.max(D[u][1], w);
            D[v][0] = Math.min(D[v][0], w);
            D[v][1] = Math.max(D[v][1], w);
        }

        Arrays.sort(G, 1, N, (a, b) -> Integer.compare(D[a[0]][1], D[b[0]][1]));

        dp[0] = 0;
        for (int i = 1; i <= N; i++) {
            dp[i] = Math.max(dp[i - 1], D[G[i][0]][1] + D[G[i][1]][1]);
        }

        pw.println(dp[N]);
        pw.close();
    }
}
