#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007

typedef long long ll;

ll modpow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

ll inv(ll a) {
    return modpow(a, MOD - 2);
}

ll C(ll n, ll k) {
    if (k > n) return 0;
    ll res = 1;
    for (ll i = 1; i <= k; i++) {
        res = res * (n - i + 1) % MOD * inv(i) % MOD;
    }
    return res;
}

int main() {
    int X1, X2, X3, X4, X5, X6, Y1, Y2, Y3, Y4, Y5, Y6;
    scanf("%d%d%d%d%d%d%d%d%d%d%d", &X1, &X2, &X3, &X4, &X5, &X6, &Y1, &Y2, &Y3, &Y4, &Y5, &Y6);

    ll ans = 0;
    for (int x = X1; x <= X2; x++) {
        for (int y = Y1; y <= Y2; y++) {
            for (int s = X3; s <= X4; s++) {
                for (int t = Y3; t <= Y4; t++) {
                    for (int u = X5; u <= X6; u++) {
                        for (int v = Y5; v <= Y6; v++) {
                            if (x == X1 || x == X2 || x == X5 || x == X6 ||
                                y == Y1 || y == Y2 || y == Y5 || y == Y6 ||
                                s == X3 || s == X4 || s == X5 || s == X6 ||
                                t == Y3 || t == Y4 || t == Y5 || t == Y6 ||
                                u == X5 || u == X6 || v == Y5 || v == Y6) {
                                continue;
                            }
                            if (abs(x - s) + abs(s - u) + abs(y - t) + abs(t - v) != abs(x - u) + abs(y - v)) {
                                continue;
                            }
                            ans = (ans + C(X2 - X1 + 1, x - X1) * C(Y2 - Y1 + 1, y - Y1) % MOD *
                                   C(X4 - X3 + 1, s - X3) * C(Y4 - Y3 + 1, t - Y3) % MOD *
                                   C(X6 - X5 + 1, u - X5) * C(Y6 - Y5 + 1, v - Y5) % MOD) % MOD;
                        }
                    }
                }
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
