#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long binpow(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return binpow(a, MOD - 2);
}

long long C(long long n, long long k) {
    if (k > n) return 0;
    vector<long long> fact(n + 1);
    fact[0] = 1;
    for (int i = 1; i <= n; i++) fact[i] = fact[i - 1] * i % MOD;
    return fact[n] * inv(fact[k]) % MOD * inv(fact[n - k]) % MOD;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    long long x1, x2, x3, x4, x5, x6, y1, y2, y3, y4, y5, y6;
    cin >> x1 >> x2 >> x3 >> x4 >> x5 >> x6 >> y1 >> y2 >> y3 >> y4 >> y5 >> y6;

    long long ans = 0;
    for (long long i = x3 - x1 + 1; i <= x4 - x1 + 1; i++) {
        for (long long j = y3 - y1 + 1; j <= y4 - y1 + 1; j++) {
            long long dx = x5 - x1 + 1 - i;
            long long dy = y5 - y1 + 1 - j;
            if (dx < 0 || dy < 0) continue;
            long long x = x2 - x1 + 1;
            long long y = y2 - y1 + 1;
            long long z = x5 - x3 + 1;
            long long w = y5 - y3 + 1;
            ans = (ans + C(x + y - 1, x - 1) * C(z + w - 1, z - 1) % MOD * C(dx + dy - 1, dx - 1)) % MOD;
        }
    }
    cout << ans << '\n';

    return 0;
}
