import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = (int) 1e9 + 7;
    static final int MAXN = (int) 1e6 + 5;

    static long[][] C = new long[MAXN][MAXN];
    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        fac[0] = 1;
        for (int i = 1; i < MAXN; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[MAXN - 1] = pow(fac[MAXN - 1], MOD - 2);
        for (int i = MAXN - 2; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }
        for (int i = 0; i < MAXN; i++) {
            C[i][0] = 1;
            for (int j = 1; j <= i; j++) {
                C[i][j] = (C[i - 1][j] + C[i - 1][j - 1]) % MOD;
            }
        }

        StringTokenizer st = new StringTokenizer(br.readLine());
        int X1 = Integer.parseInt(st.nextToken());
        int X2 = Integer.parseInt(st.nextToken());
        int X3 = Integer.parseInt(st.nextToken());
        int X4 = Integer.parseInt(st.nextToken());
        int X5 = Integer.parseInt(st.nextToken());
        int X6 = Integer.parseInt(st.nextToken());

        st = new StringTokenizer(br.readLine());
        int Y1 = Integer.parseInt(st.nextToken());
        int Y2 = Integer.parseInt(st.nextToken());
        int Y3 = Integer.parseInt(st.nextToken());
        int Y4 = Integer.parseInt(st.nextToken());
        int Y5 = Integer.parseInt(st.nextToken());
        int Y6 = Integer.parseInt(st.nextToken());

        long ans = 0;
        for (int i = X5; i <= X6; i++) {
            for (int j = Y5; j <= Y6; j++) {
                long x = (C[X6 - X5 + X4 - X3][X4 - X3] * C[Y6 - Y5 + Y4 - Y3][Y4 - Y3]) % MOD;
                long y = (C[X6 - X5 + X4 - X3][X4 - X3] * C[Y6 - Y5 + Y4 - Y3][Y4 - Y3] * C[X4 - X3 + X2 - X1][X2 - X1]) % MOD;
                long z = (C[X6 - X5 + X4 - X3][X4 - X3] * C[Y6 - Y5 + Y4 - Y3][Y4 - Y3] * C[X4 - X3 + X2 - X1][X2 - X1] * C[X2 - X1 + X4 - X3][X4 - X3]) % MOD;
                ans = (ans + x * (fac[X6 - X5] * fac[Y6 - Y5] % MOD) % MOD) % MOD;
                ans = (ans + y * (fac[X6 - X5] * fac[Y6 - Y5] % MOD) % MOD * (X6 - X5 + X4 - X3 - X2 + X1) % MOD) % MOD;
                ans = (ans + z * (fac[X6 - X5] * fac[Y6 - Y5] % MOD) % MOD * (fac[X6 - X5 + X4 - X3 + X2 - X1] * fac[Y6 - Y5 + Y4 - Y3 + X2 - X1] % MOD) % MOD) % MOD;
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
