#include <stdio.h>
#include <stdlib.h>

#define N 100005

int n, a[N], b[N], fa[N], dep[N], col[N], ans[N];

void dfs1(int u, int f) {
    fa[u] = f;
    dep[u] = dep[f] + 1;
    for (int i = a[u]; i != -1; i = a[i]) {
        dfs1(i, u);
    }
}

void dfs2(int u, int c) {
    col[u] = c;
    for (int i = b[u]; i != -1; i = b[i]) {
        dfs2(i, c);
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 1; i <= n; i++) {
        scanf("%d", &b[i]);
    }
    dfs1(1, 0);
    int cnt = 0;
    for (int i = 1; i <= n; i++) {
        if (dep[i] > dep[fa[i]]) {
            cnt++;
        }
    }
    if (cnt > 2) {
        printf("IMPOSSIBLE\n");
        return 0;
    }
    dfs2(1, 1);
    for (int i = 1; i <= n; i++) {
        if (col[i] == 1) {
            ans[i] = 1;
        } else {
            ans[i] = -1;
        }
    }
    for (int i = 1; i <= n; i++) {
        if (col[i] == 1) {
            for (int j = a[i]; j != -1; j = a[j]) {
                ans[j] = -ans[j];
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (col[i] == 2) {
            for (int j = b[i]; j != -1; j = b[j]) {
                ans[j] = -ans[j];
            }
        }
    }
    printf("POSSIBLE\n");
    for (int i = 1; i <= n; i++) {
        printf("%d ", ans[i]);
    }
    printf("\n");
    return 0;
}
