#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, a[N], b[N], d[N], c[N];
vector<int> g[N];

void dfs(int u, int p, int t) {
    d[u] = d[p] + 1;
    c[u] = t;
    for (int v : g[u]) {
        if (v != p) {
            dfs(v, u, t ^ 1);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
        a[i]--;
        g[a[i]].push_back(i);
    }
    for (int i = 1; i <= n; i++) {
        cin >> b[i];
        b[i]--;
        g[b[i]].push_back(i);
    }

    dfs(0, -1, 0);
    queue<int> q;
    for (int i = 0; i <= n; i++) {
        if (g[i].size() == 0) {
            q.push(i);
        }
    }

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            g[a[v]].erase(find(g[a[v]].begin(), g[a[v]].end(), v));
            g[b[v]].erase(find(g[b[v]].begin(), g[b[v]].end(), v));
            if (g[a[v]].size() == 0) {
                q.push(a[v]);
            }
            if (g[b[v]].size() == 0) {
                q.push(b[v]);
            }
        }
    }

    for (int i = 0; i <= n; i++) {
        if (g[i].size() > 0) {
            cout << "IMPOSSIBLE\n";
            return 0;
        }
    }

    cout << "POSSIBLE\n";
    for (int i = 1; i <= n; i++) {
        if (d[i] % 2 == 0) {
            cout << 1 << " ";
        } else {
            cout << -1 << " ";
        }
    }
    cout << "\n";

    return 0;
}
