import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] A, B;
    static int[] color;
    static int[] depth;
    static int[] X;
    static List<Integer>[] adj;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());

        A = new int[N];
        B = new int[N];
        color = new int[N];
        depth = new int[N];
        X = new int[N];
        adj = new List[N];

        for (int i = 0; i < N; i++) {
            adj[i] = new ArrayList<>();
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            A[i] = Integer.parseInt(st.nextToken());
            A[i]--;
            adj[A[i]].add(i);
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            B[i] = Integer.parseInt(st.nextToken());
            B[i]--;
            adj[B[i]].add(i);
        }

        dfs(0, 0, 0);
        dfs1(0, 0, 0);

        if (color[0] == 0) {
            pw.println("POSSIBLE");
            for (int i = 0; i < N; i++) {
                pw.print(X[i] + " ");
            }
            pw.println();
        } else {
            pw.println("IMPOSSIBLE");
        }

        pw.close();
    }

    static void dfs(int u, int p, int c) {
        color[u] = c;
        depth[u] = depth[p] + 1;
        for (int v : adj[u]) {
            if (v != p) {
                dfs(v, u, c ^ 1);
            }
        }
    }

    static void dfs1(int u, int p, int c) {
        if (color[u] == c) {
            X[u] = 1;
        } else {
            X[u] = -1;
        }
        for (int v : adj[u]) {
            if (v != p) {
                dfs1(v, u, c);
            }
        }
    }
}
