from collections import deque

def dfs1(u, p, tree, color):
    color[u] = 1
    for v in tree[u]:
        if v != p:
            dfs1(v, u, tree, color)

def dfs2(u, p, tree, color):
    color[u] = 2
    for v in tree[u]:
        if v != p:
            dfs2(v, u, tree, color)

def solve():
    n = int(input())
    a = list(map(lambda x: int(x) - 1, input().split()))
    b = list(map(lambda x: int(x) - 1, input().split()))
    tree1 = [[] for _ in range(n)]
    tree2 = [[] for _ in range(n)]
    for i in range(n):
        tree1[a[i]].append(i)
        tree2[b[i]].append(i)
    color = [0] * n
    root1 = -1
    root2 = -1
    for i in range(n):
        if a[i] == -1:
            root1 = i
        if b[i] == -1:
            root2 = i
    if root1 == -1 and root2 == -1:
        print("IMPOSSIBLE")
        return
    if root1 != -1:
        dfs1(root1, -1, tree1, color)
    if root2 != -1:
        dfs2(root2, -1, tree2, color)
    ans = [0] * n
    for i in range(n):
        if color[i] == 1:
            ans[i] = 1
        elif color[i] == 2:
            ans[i] = -1
    for i in range(n):
        for v in tree1[i]:
            if ans[v] == 0:
                ans[v] = -ans[i]
        for v in tree2[i]:
            if ans[v] == 0:
                ans[v] = -ans[i]
    if any(abs(sum(ans[j] for j in tree1[i]) + sum(ans[j] for j in tree2[i])) != 1 for i in range(n)):
        print("IMPOSSIBLE")
        return
    print("POSSIBLE")
    print(*ans)

solve()
